import React from "react";
import styled from "styled-components";
import Link from "Components/Link";
import spacing from "tokens/spacing";
import PropTypes from "prop-types";
import { grey } from "ZColors";
import { link as linkColor } from "tokens/colors";

const Breadcrumb = props => {
  return <Breadcrumb.Ele {...props}>{props.children}</Breadcrumb.Ele>;
};

Breadcrumb.Separator = styled.span`
  margin-left: ${spacing.xsmall};
  color: ${grey.z300};
  cursor: default;
  font-size: 1.2rem;
`;

Breadcrumb.Ele = styled.div`
  font-size: 1.2rem;
  ${Link.Ele} {
    margin-right: ${spacing.xsmall};
    font-weight: 300;
  }

  ${Link.Ele}:last-child {
    margin-right: 0;
    color: ${grey.z500};
    cursor: default;
    pointer-events: none;
  }

  ${Link.Ele}:last-child ${Breadcrumb.Separator} {
    display: none;
  }
  ${Link.Ele}:first-child {
    color: ${linkColor.defaultColor};
  }
`;

Breadcrumb.Link = props => (
  <Link {...props}>
    {/* eslint-disable-next-line react/prop-types */}
    {props.children}
    <Breadcrumb.Separator>/</Breadcrumb.Separator>
  </Link>
);

Breadcrumb.propTypes = {
  children: PropTypes.element.isRequired
};

Breadcrumb.Link.propTypes = {
  linkTo: PropTypes.string
};

export default Breadcrumb;
