import React, { lazy, Suspense } from "react";
import Spinner from "../Spinner";

const DateRange = lazy(() => import("./DateRange"));

const DateRangeWrapper = props => (
  <div>
    <Suspense fallback={<Spinner />}>
      <DateRange {...props} />
    </Suspense>
  </div>
);

export default DateRangeWrapper;
