import {
  imgUploaderError as errorColors,
  imgUploaderDefault as defaultColors
} from "../../tokens/colors";
import {
  imgUploaderError as errorSizes,
  imgUploaderDefault as defaultSizes
} from "../../tokens/sizes";

const getAttributes = key => ({ error }) => {
  const attributes = error
    ? { ...errorColors, ...errorSizes }
    : { ...defaultColors, ...defaultSizes };

  return error ? attributes[key] : attributes[key];
};

const getOpacity = ({ hover }) => (hover ? 0.6 : 1);

export { getAttributes, getOpacity };
