import { checkbox, checkboxSelected } from "../../../tokens/colors";

const getClickedAttr = cbType => checkboxSelected[cbType];

const getAttr = disabled => (disabled ? checkbox.disabled : checkbox.default);

const getClickedAttributes = key => ({ cbType }) => getClickedAttr(cbType)[key];

const getAttributes = key => ({ disabled }) => getAttr(disabled)[key];

const getType = (disabled, solid) => {
  const key = [];
  if (disabled) {
    key.push("disabled");
  }
  if (solid) {
    key.push("solid");
  }

  return key.length ? key.join("_") : "default";
};

export { getAttributes, getClickedAttributes, getType };
