import styled from "styled-components";
import spacing from "sauce/tokens/spacing";
import { black } from "ZColors";

const SwitchLabel = styled.label`
  height: ${props => props.height};
  span {
    display: block;
    font-size: 12px;
    font-weight: 400;
    letter-spacing: 1px;
    text-transform: uppercase;
    color: ${black};
    margin-left: ${props => (props.align == "left" ? "0" : spacing.small)};
    margin-right: ${props => (props.align == "left" ? spacing.small : "0")};
    text-align: ${props => (props.align == "left" ? "right" : "left")};
    /*
  In order to make the switch always the same width
  we are setting a fixed height and overlapping the switch labels
  */
    height: ${props => props.height};
    line-height: ${props => props.height};
    &:last-child {
      /* Moves the second label up to overlap the first */
      margin-top: calc(${props => props.height} * -1);
    }
    &[aria-hidden="true"] {
      visibility: hidden;
    }
    /* if the label is empty, then remove the node so it doesn't create a margin */
    &:empty {
      display: none;
    }
  }
`;

export default SwitchLabel;
