import React from "react";
import styled from "styled-components";
import { white } from "ZColors";
import PropTypes from "prop-types";

const Header = ({ children, logoImg }) => (
  <Header.wrapper>
    <Header.Element>
      <Header.Logo src={logoImg} />
      {children}
    </Header.Element>
  </Header.wrapper>
);

Header.wrapper = styled.header`
  width: 100%;
  max-width: 100%;
  display: flex;
  height: 7.2rem;
  justify-content: center;
  align-items: center;
  background-color: ${white};
`;

Header.Element = styled.div`
  width: 100%;
  max-width: 90rem;
  margin: 0 auto;
  display: flex;
  flex-wrap: nowrap;
  height: 100%;
  justify-content: space-between;
  align-items: center;
`;

Header.Logo = styled.img`
  max-width: 14rem;

  height: 2.7rem;
`;

Header.propTypes = {
  children: PropTypes.oneOfType([
    PropTypes.arrayOf(PropTypes.node),
    PropTypes.node
  ]).isRequired,
  logoImg: PropTypes.string.isRequired
};

export default Header;
