const unit = 4;

const spacing = {
  unit,
  xxsmall: unit * 0.1 + "rem",
  xsmall: unit * 0.2 + "rem",
  small: unit * 0.4 + "rem",
  medium: unit * 0.6 + "rem",
  large: unit * 0.8 + "rem",
  xlarge: unit * 1.2 + "rem"
};

const zSpacing = {
  z0: 0,
  z1: unit * 0.1 + "rem",
  z2: unit * 0.2 + "rem",
  z3: unit * 0.4 + "rem",
  z4: unit * 0.6 + "rem",
  z5: unit * 0.8 + "rem",
  z6: unit + "rem"
};

export default spacing;
export { zSpacing };
