import React from "react";
import { storiesOf } from "@storybook/react";
import Stack from "sauce/helpers/story-stack";
import Example from "sauce/helpers/story-example";
import Breadcrumb from "Components/Breadcrumb";
import withDocs from "storybook-readme/with-docs";
import breadcrumbReadMe from "Components/Breadcrumb/BREADCRUMB.md";

storiesOf("Breadcrumb", module).add(
  "Info",
  withDocs(breadcrumbReadMe, () => (
    <div>
      <Example title="Multiple Links">
        <Stack>
          <Breadcrumb>
            <Breadcrumb.Link linkTo="/home">home</Breadcrumb.Link>
            <Breadcrumb.Link linkTo="/ncr">ncr</Breadcrumb.Link>
            <Breadcrumb.Link linkTo="/res">beer cafe</Breadcrumb.Link>
          </Breadcrumb>
        </Stack>
      </Example>
      <Example title="Single link">
        <Stack>
          <Breadcrumb>
            <Breadcrumb.Link linkTo="/home">home</Breadcrumb.Link>
          </Breadcrumb>
        </Stack>
      </Example>
    </div>
  ))
);
