import React from "react";
import { storiesOf } from "@storybook/react";
import Checkbox from "Components/InputTypes/Checkbox";
import withDocs from "storybook-readme/with-docs";
import CheckboxReadme from "Components/InputTypes/Checkbox/README.md";

storiesOf("Checkbox", module)
  .add(
    "Introduction",
    withDocs(CheckboxReadme, () => <Checkbox label="Click Me" />)
  )
  .addWithChapters("Checkbox Demo", {
    subtitle: "Different state and props of Checkbox component.",
    chapters: [
      {
        title: "Basic Checkbox",
        info: `
        ~~~js
        import Checkbox from "WebUIKit/Checkbox";
        ~~~
        `,
        sections: [
          {
            title: "Basic",
            sectionFn: () => {
              const onChange = value => {};
              return <Checkbox label="Solid" onChange={onChange} />;
            },
            options: {
              showSource: false,
              allowSourceToggling: true,
              showPropTables: false,
              allowPropTablesToggling: false
            }
          },
          {
            title: "Hollow State",
            sectionFn: () => {
              const onChange = value => {};
              return (
                <Checkbox label="Hollow" solid={false} onChange={onChange} />
              );
            },
            options: {
              showSource: false,
              allowSourceToggling: true,
              showPropTables: false,
              allowPropTablesToggling: false
            }
          },
          {
            title: "Disabled State",
            sectionFn: () => {
              const onChange = value => {};
              return <Checkbox label="Disabled" disabled onChange={onChange} />;
            },
            options: {
              showSource: false,
              allowSourceToggling: true,
              showPropTables: false,
              allowPropTablesToggling: false
            }
          },
          {
            title: "Checked Disabled State",
            sectionFn: () => {
              const onChange = value => {};
              return (
                <Checkbox
                  label="Disabled"
                  disabled
                  onChange={onChange}
                  checked
                />
              );
            },
            options: {
              showSource: false,
              allowSourceToggling: true,
              showPropTables: false,
              allowPropTablesToggling: false
            }
          },
          {
            title: "Checked Disabled Hollow State",
            sectionFn: () => {
              const onChange = value => {};
              return (
                <Checkbox
                  label="Disabled"
                  solid={false}
                  disabled
                  onChange={onChange}
                  checked
                />
              );
            },
            options: {
              showSource: false,
              allowSourceToggling: true,
              showPropTables: false,
              allowPropTablesToggling: false
            }
          }
        ]
      }
    ]
  });
