import React from "react";
import { storiesOf } from "@storybook/react";
import DateRange from "Components/DateRange";
import withDocs from "storybook-readme/with-docs";
import DateRangeReadme from "Components/DateRange/README.md";

storiesOf("DateRangePicker", module)
  .add("Introduction", withDocs(DateRangeReadme, () => <DateRange />))
  .addWithChapters("DateRange Demo", {
    subtitle: "Different state and props of DateRange component.",
    chapters: [
      {
        title: "Basic DateRange",
        info: `
        ~~~js
        import DateRange from "WebUIKit/DateRange";
        ~~~
        `,
        sections: [
          {
            title: "Basic",
            sectionFn: () => {
              return <DateRange />;
            },
            options: {
              showSource: false,
              allowSourceToggling: true,
              showPropTables: false,
              allowPropTablesToggling: false
            }
          },
          {
            title: "Different Color",
            sectionFn: () => {
              return <DateRange color="#999" rangeColor="#ddd" />;
            },
            options: {
              showSource: false,
              allowSourceToggling: true,
              showPropTables: false,
              allowPropTablesToggling: false
            }
          },
          {
            title: "Without Dates",
            sectionFn: () => {
              return <DateRange showDateDisplay={false} />;
            },
            options: {
              showSource: false,
              allowSourceToggling: true,
              showPropTables: false,
              allowPropTablesToggling: false
            }
          },
          {
            title: "Date Range Picker",
            sectionFn: () => {
              return <DateRange showRangePicker />;
            },
            options: {
              showSource: false,
              allowSourceToggling: true,
              showPropTables: false,
              allowPropTablesToggling: false
            }
          }
        ]
      }
    ]
  });
