import React from "react";
import { storiesOf } from "@storybook/react";
import Input from "Components/InputTypes/Input";
import withDocs from "storybook-readme/with-docs";
import InputReadme from "Components/InputTypes/Input/README.md";

storiesOf("Input", module)
  .add("Introduction", withDocs(InputReadme, () => <Input label="Name" />))
  .addWithChapters("Input Demo", {
    subtitle: "Different state and props of Input component.",
    chapters: [
      {
        title: "Basic Input",
        info: `
        ~~~js
        import Input from "WebUIKit/Input";
        ~~~
        `,
        sections: [
          {
            title: "Basic",
            sectionFn: () => {
              const onChange = value => {};
              return <Input label="Name" width="300px" onChange={onChange} />;
            },
            options: {
              showSource: false,
              allowSourceToggling: true,
              showPropTables: false,
              allowPropTablesToggling: false
            }
          },
          {
            title: "Without Auto complete",
            sectionFn: () => {
              const onChange = value => {};
              return (
                <Input
                  label="Name"
                  width="300px"
                  onChange={onChange}
                  autocomplete={false}
                />
              );
            },
            options: {
              showSource: false,
              allowSourceToggling: true,
              showPropTables: false,
              allowPropTablesToggling: false
            }
          },
          {
            title: "With icon",
            sectionFn: () => {
              const onChange = value => {};
              return (
                <Input
                  label="Name"
                  width="300px"
                  icon="star-empty"
                  iconColor="#B5B5B5"
                  onChange={onChange}
                />
              );
            },
            options: {
              showSource: false,
              allowSourceToggling: true,
              showPropTables: false,
              allowPropTablesToggling: false
            }
          },
          {
            title: "With helper text",
            sectionFn: () => {
              const onChange = value => {};
              return (
                <Input
                  label="Name"
                  width="300px"
                  icon="star-empty"
                  iconColor="#B5B5B5"
                  helperText="Write your name here"
                  onChange={onChange}
                />
              );
            },
            options: {
              showSource: false,
              allowSourceToggling: true,
              showPropTables: false,
              allowPropTablesToggling: false
            }
          },
          {
            title: "Disabled State",
            sectionFn: () => (
              <Input
                label="Disabled with helper text"
                helperText="This is disabled"
                width="300px"
                disabled
              />
            ),
            options: {
              showSource: false,
              allowSourceToggling: true,
              showPropTables: false,
              allowPropTablesToggling: false
            }
          },
          {
            title: "Disabled State with Icon",
            sectionFn: () => (
              <Input
                disabled
                label="Disabled with icon"
                width="300px"
                icon="star-empty"
                iconColor="#B5B5B5"
              />
            ),
            options: {
              showSource: false,
              allowSourceToggling: true,
              showPropTables: false,
              allowPropTablesToggling: false
            }
          },
          {
            title: "Disabled State with default value",
            sectionFn: () => {
              return (
                <Input
                  disabled
                  label="Disabled"
                  value="Default Value"
                  width="300px"
                />
              );
            },
            options: {
              showSource: false,
              allowSourceToggling: true,
              showPropTables: false,
              allowPropTablesToggling: false
            }
          }
        ]
      }
    ]
  });
