import React from "react";
import { storiesOf } from "@storybook/react";
import RadioGroup from "Components/RadioGroup";
import withDocs from "storybook-readme/with-docs";
import RadioGroupReadme from "Components/RadioGroup/README.md";

const options = [
  {
    label: "Spicy",
    value: "opt_1"
  },
  {
    label: "Mild",
    value: "opt_2"
  },
  {
    label: "Bland",
    value: "opt_3"
  }
];

const disabledOptions = [
  {
    label: "Disabled Option 1",
    value: "opt_1",
    disabled: true
  },
  {
    label: "Disabled Option 2",
    value: "opt_2",
    disabled: true
  },
  {
    label: "Disabled, Selected Option",
    value: "opt_3",
    disabled: true
  }
];

storiesOf("RadioGroup", module)
  .add(
    "Introduction",
    withDocs(RadioGroupReadme, () => (
      <RadioGroup options={options} name="radio" />
    ))
  )
  .addWithChapters("RadioGroup Demo", {
    subtitle: "Different state and props of RadioGroup component.",
    chapters: [
      {
        title: "Basic RadioGroup",
        info: `
        ~~~js
        import RadioGroup from "WebUIKit/RadioGroup";
        ~~~
        `,
        sections: [
          {
            title: "Basic",
            sectionFn: () => <RadioGroup options={options} name="group1" />,
            options: {
              showSource: false,
              allowSourceToggling: true,
              showPropTables: false,
              allowPropTablesToggling: false
            }
          },
          {
            title: "Horizontal RadioGroup",
            sectionFn: () => (
              <RadioGroup options={options} name="group2" horizontal />
            ),
            options: {
              showSource: false,
              allowSourceToggling: true,
              showPropTables: false,
              allowPropTablesToggling: false
            }
          },
          {
            title: "Radio buttons with default value",
            sectionFn: () => (
              <RadioGroup
                options={options}
                defaultValue={"opt_2"}
                name="group3"
              />
            ),
            options: {
              showSource: false,
              allowSourceToggling: true,
              showPropTables: false,
              allowPropTablesToggling: false
            }
          },
          {
            title: "Disabled with default value Radio buttons",
            sectionFn: () => (
              <RadioGroup
                options={disabledOptions}
                defaultValue={"opt_3"}
                name="group4"
              />
            ),
            options: {
              showSource: false,
              allowSourceToggling: true,
              showPropTables: false,
              allowPropTablesToggling: false
            }
          }
        ]
      }
    ]
  });
