import React from "react";
import { storiesOf } from "@storybook/react";
import Slider from "Components/Slider";
import withDocs from "storybook-readme/with-docs";
import SliderReadme from "Components/Slider/README.md";

storiesOf("Slider", module)
  .add("Introduction", withDocs(SliderReadme, () => <Slider value={0} />))
  .addWithChapters("Slider Demo", {
    subtitle: "Different state and props of Slider component.",
    chapters: [
      {
        title: "Basic Slider",
        info: `
        ~~~js
        import Slider from "WebUIKit/Slider";
        ~~~
        `,
        sections: [
          {
            title: "Basic",
            sectionFn: () => {
              const onChange = _ => {};
              return (
                <Slider
                  maxValue={20}
                  minValue={0}
                  value={0}
                  onChange={onChange}
                />
              );
            },
            options: {
              showSource: false,
              allowSourceToggling: true,
              showPropTables: false,
              allowPropTablesToggling: false
            }
          },
          {
            title: "Double Value",
            sectionFn: () => {
              const onChange = _ => {};
              return (
                <Slider
                  maxValue={20}
                  minValue={0}
                  value={{ min: 2, max: 10 }}
                  onChange={onChange}
                />
              );
            },
            options: {
              showSource: false,
              allowSourceToggling: true,
              showPropTables: false,
              allowPropTablesToggling: false
            }
          }
        ]
      }
    ]
  });
