import React from "react";
import { storiesOf } from "@storybook/react";
import Textarea from "Components/InputTypes/Textarea";
import withDocs from "storybook-readme/with-docs";
import TextareaReadme from "Components/InputTypes/Textarea/README.md";

storiesOf("Textarea", module)
  .add(
    "Introduction",
    withDocs(TextareaReadme, () => <Textarea label="Description" />)
  )
  .addWithChapters("Textarea Demo", {
    subtitle: "Different state and props of Textarea component.",
    chapters: [
      {
        title: "Basic Textarea",
        info: `
        ~~~js
        import Textarea from "WebUIKit/Textarea";
        ~~~
        `,
        sections: [
          {
            title: "Basic",
            sectionFn: () => {
              const onChange = value => {};
              return <Textarea label="Description" onChange={onChange} />;
            },
            options: {
              showSource: false,
              allowSourceToggling: true,
              showPropTables: false,
              allowPropTablesToggling: false
            }
          },
          {
            title: "Disabled",
            sectionFn: () => <Textarea label="Description" disabled />,
            options: {
              showSource: false,
              allowSourceToggling: true,
              showPropTables: false,
              allowPropTablesToggling: false
            }
          },
          {
            title: "Non resizable enabled Textarea Input",
            sectionFn: () => (
              <Textarea label="Description" resize="none" cols={40} />
            ),
            options: {
              showSource: false,
              allowSourceToggling: true,
              showPropTables: false,
              allowPropTablesToggling: false
            }
          },
          {
            title: "Non resizable disabled Textarea Input",
            sectionFn: () => (
              <Textarea disabled label="Disabled" resize="none" cols={40} />
            ),
            options: {
              showSource: false,
              allowSourceToggling: true,
              showPropTables: false,
              allowPropTablesToggling: false
            }
          },
          {
            title: "Non resizable disabled Textarea Input with default value",
            sectionFn: () => (
              <Textarea
                disabled
                label="Disabled"
                value="Disabled Example"
                resize="none"
                cols={40}
              />
            ),
            options: {
              showSource: false,
              allowSourceToggling: true,
              showPropTables: false,
              allowPropTablesToggling: false
            }
          }
        ]
      }
    ]
  });
