import React from "react";
import { storiesOf } from "@storybook/react";
import Tooltip from "Components/Tooltip";
import Example from "sauce/helpers/story-example";
import Wrapper from "./genericWrapper";

storiesOf("Tooltip", module)
  .add("Top", () => (
    <Example>
      <Wrapper>
        <Tooltip label="Im a tooltip!">
          <div>Top Big</div>
        </Tooltip>
      </Wrapper>

      <Wrapper>
        <Tooltip label="Im a tooltip!" appearance="light">
          <div>Top Big Light</div>
        </Tooltip>
      </Wrapper>
    </Example>
  ))
  .add("Left", () => (
    <Example>
      <Wrapper>
        <Tooltip label="Im a tooltip!" position="left">
          <div>Left Big</div>
        </Tooltip>
      </Wrapper>

      <Wrapper>
        <Tooltip label="Im a tooltip!" appearance="light" position="left">
          <div>Left Big Light</div>
        </Tooltip>
      </Wrapper>
    </Example>
  ))
  .add("Bottom", () => (
    <Example>
      <Wrapper>
        <Tooltip label="Im a tooltip!" position="bottom">
          <div>Bottom Big</div>
        </Tooltip>
      </Wrapper>

      <Wrapper>
        <Tooltip label="Im a tooltip!" appearance="light" position="bottom">
          <div>Bottom Big Light</div>
        </Tooltip>
      </Wrapper>
    </Example>
  ))
  .add("Right", () => (
    <Example>
      <Wrapper>
        <Tooltip label="Im a tooltip!" position="right">
          <div>Bottom Big</div>
        </Tooltip>
      </Wrapper>

      <Wrapper>
        <Tooltip label="Im a tooltip!" appearance="light" position="right">
          <div>Bottom Big Light</div>
        </Tooltip>
      </Wrapper>
    </Example>
  ))
  .add("Small", () => (
    <Example>
      <Wrapper>
        <Tooltip label="69" position="top" size="small">
          <div>Small</div>
        </Tooltip>
      </Wrapper>
    </Example>
  ));
